import { createStore } from "vuex";

const store = createStore({
  state: {
    products: [],
    cart: 0,
    faq: [
      {
        title: "Porta non pulvinar neque laoreet ?",
        text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Pharetra massa massa ultricies mi quis hendrerit dolor. Vulputate mi sit amet mauris commodo. Diam phasellus vestibulum lorem sed risus ultricies tristique nulla. In egestas erat imperdiet sed. Purus sit amet volutpat consequat mauris nunc. Molestie at elementum eu facilisis sed. Imperdiet proin fermentum leo vel orci. At auctor urna nunc id. Quam viverra orci sagittis eu volutpat odio. Quam pellentesque nec nam aliquam sem et tortor consequat. Lacinia at quis risus sed. Sit amet venenatis urna cursus. Egestas pretium aenean pharetra magna ac placerat vestibulum lectus mauris. Dignissim convallis aenean et tortor. Laoreet suspendisse interdum consectetur libero id faucibus nisl tincidunt eget. Amet commodo nulla facilisi nullam. Odio eu feugiat pretium nibh ipsum consequat nisl vel pretium. Tellus cras adipiscing enim eu turpis. Diam quam nulla porttitor massa id neque aliquam vestibulum morbi.",
      },
      {
        title:
          "Interdum velit laoreet id donec ultrices tincidunt arcu non sodales ?",
        text: "https://www.behance.net/crztho",
      },
      {
        title: "Interdum posuere lorem ipsum dolor sit amet ?",
        text: "https://www.behance.net/crztho",
      },
    ],
  },
  mutations: {
    setProducts(state, products) {
      state.products = products;
    },
  },
  actions: {
    fetchAllProducts(context) {
      fetch("https://fakestoreapi.com/products?limit=4")
        .then((response) => response.json())
        .then((json) => {
          context.commit("setProducts", json);
        })
        .catch((err) => console.error(err));
    },
  },

  getters: {
    products(state) {
      return state.products;
    },
  },
});
export default store;
