import Vuex from "vuex";

export default new Vuex.Store({
  state: {
    allProducts: [],
  },

  actions: {
    async fetchAllProducts({ commit }) {
      const AllProducts = await fetch("https://fakestoreapi.com/products/1");
      await commit("updateAllProducts", AllProducts.data);
    },
  },

  mutations: {
    updateAllProducts(state, data) {
      state.AllProducts = data;
    },
  },
});
